                    ,issold
                    ,item_id)
VALUES ( #{id}
        ,NOW()
        ,DATE_ADD(NOW(), INTERVAL 1 DAY)
        ,#{bid.price}
        ,#{bid.startPrice}
        ,#{bid.startPrice}
        ,#{sellerID}
        ,#{bid.isSold}
        ,#{itemID});
