mapper.select:
SELECT COUNT(*) as id
FROM user
WHERE user_id = #{userID}

SELECT IFNULL(id,0) as id
FROM user
WHERE user_id = #{userID}
AND password = #{userPassword}

mapper.insert:

    SELECT IFNULL(MAX(id), 0) + 1 FROM user

INSERT INTO user (   id
                    ,user_id
                    ,password
                    ,nickname
                    ,phone_number
                    ,gold
                    ,user_level
                    ,isAdmin
                    ,isAbusing)
VALUES ( #{id}
        ,#{userID}
        ,#{password}
        ,#{nickname}
        ,#{phoneNumber}
        ,#{gold}
        ,#{userLevel}
        ,#{isAdmin}
        ,#{isAbusing});
