mapper.select:
SELECT  id
FROM    bid
WHERE   item_id = #{itemId}

mapper.insert:
SELECT LAST_INSERT_ID()
INSERT INTO bid ( create_time, limit_time, price, start_price, present_price, seller_id, sale_status, item_id)
VALUES ( NOW(), DATE_ADD(NOW(), INTERVAL 1 DAY), #{price}, #{startPrice}, #{startPrice}, #{sellerId}, 'SALE', #{itemId});

mapper.update:
UPDATE      user
SET         gold    = gold + #{priceGold}
WHERE       user_id = #{userId}
