                    ,#{equipmentLevel}
                    ,#{rating}
                    ,#{mainType}
                    ,#{subType}
                    ,#{property1}
                    ,#{property2}
                    ,#{itemImageURL}
                    ,#{userId}
                    ,#{exist});
