mapper.insert:

    SELECT LAST_INSERT_ID()

INSERT INTO item (   name
                    ,equipment_level
                    ,rating
                    ,main_type
                    ,sub_type
                    ,first_property
                    ,second_property
                    ,item_image_url
                    ,user_id
                    ,is_Exist )

VALUE          (     #{name}
                    ,#{equipmentLevel}
                    ,#{rating}
                    ,#{mainType}
                    ,#{subType}
                    ,#{firstProperty}
                    ,#{secondProperty}
                    ,#{itemImageURL}
                    ,#{userId}
                    ,#{exist});

mapper.select:
SELECT  i.id
FROM    item i
JOIN    user u ON i.user_id = u.id
WHERE   i.id = #{itemId}
AND     u.user_id = #{userId}
