mapper.insert:

    SELECT IFNULL(MAX(id), 0) + 1 FROM item

INSERT INTO item (   id
                    ,name
                    ,equipment_level
                    ,rating
                    ,main_type
                    ,sub_type
                    ,property1
                    ,property2
                    ,item_image_url
                    ,user_id
                    ,is_Exist)
VALUE (     #{id}
            ,#{name}
            ,#{equipmentLevel}
            ,#{rating}
            ,#{mainType}
            ,#{subType}
            ,#{property1}
            ,#{property2}
            ,#{itemImageURL}
            ,#{userID}
            ,#{exist});
