mapper.insert:
SELECT LAST_INSERT_ID()
INSERT INTO transactionhistory ( seller_id, buyer_id, sale_time, item_id, trade_price) VALUES
( #{transactionHistory.sellerId}, #{transactionHistory.highestBidderId}, NOW(), #{transactionHistory.itemId},
#{transactionHistory.price}
#{transactionHistory.presentPrice}
)

mapper.select:
SELECT history.id, history.seller_id AS sellerId, history.buyer_id AS buyerId, history.sale_time AS saleTime, history.trade_price AS tradePrice, history.item_id AS itemId FROM transactionhistory history
JOIN item i ON history.item_id = i.id WHERE i.name = #{itemName}
WHERE history.seller_id = #{userId} OR history.buyer_id = #{userId}
