mapper.insert:
SELECT LAST_INSERT_ID()
INSERT INTO user ( user_id, password, nickname, phone_number, gold, user_level, user_type )
VALUES ( #{userId}, #{password}, #{nickname}, #{phoneNumber}, #{gold}, #{userLevel}, #{userType} );

mapper.select:
SELECT      IFNULL(id,0)    AS id
FROM        user
WHERE       user_id         = #{userId}
AND         password        = #{password}

mapper.update:
UPDATE  user
SET

            nickname = #{nickname},


            phone_number = #{phoneNumber}

WHERE   user_id = #{userId}
